(function(e) {
    var t = {
        position: "top",
        height: "5px",
        col_1: "#159756",
        col_2: "#da4733",
        col_3: "#3b78e7",
        col_4: "#fdba2c",
        fadeIn: 200,
        fadeOut: 200,
        placeHolder: "body",
    };
    e.materialPreloader = function(n) {
        var r = e.extend({}, t, n);
        $template = "<div id='materialPreloader' class='load-bar' style='height:" + r.height + ";display:block;" + r.position + ":0px;z-index:9999'><div class='load-bar-container'><div class='load-bar-base base1' style='background:" + r.col_1 + "'><div class='color red' style='background:" + r.col_2 + "'></div><div class='color blue' style='background:" + r.col_3 + "'></div><div class='color yellow' style='background:" + r.col_4 + "'></div><div class='color green' style='background:" + r.col_1 + "'></div></div></div> <div class='load-bar-container'><div class='load-bar-base base2' style='background:" + r.col_1 + "'><div class='color red' style='background:" + r.col_2 + "'></div><div class='color blue' style='background:" + r.col_3 + "'></div><div class='color yellow' style='background:" + r.col_4 + "'></div> <div class='color green' style='background:" + r.col_1 + "'></div> </div> </div> </div>";
        $(r.placeHolder+" #materialPreloader").remove();
        e(r.placeHolder).prepend($template);
        this.on = function(placeHolder) {
            e(placeHolder+" #materialPreloader").fadeIn(r.fadeIn)
        }
        ;
        this.off = function(placeHolder) {
           if(placeHolder == null){
                placeHolder = "body";
            }
            e(placeHolder+" #materialPreloader").fadeOut(r.fadeOut)
        }
    }
})(jQuery)